package com.example.discover.ui.UserCountryActivity;

import androidx.lifecycle.MutableLiveData;
import androidx.lifecycle.ViewModel;

import com.example.discover.pojo.Country;

import java.util.ArrayList;

public class CountriesViewModel extends ViewModel {
    MutableLiveData<ArrayList<Country>> countriesListLiveData = new MutableLiveData<>();
    private ArrayList<Country> countriesList = new ArrayList<>();

    private void setCountriesList(){
        countriesList.add(new Country("United States of America", "us","data:image/png;base64,"));
        countriesList.add(new Country("United Arab Emirates" , "ae", "data:image/png;base64,"));
        countriesList.add(new Country("China", "cn","data:image/png;base64,"));
        countriesList.add(new Country("Egypt", "eg","data:image/png;base64,"));
        countriesList.add(new Country("France", "fr","data:image/png;base64,"));
        countriesList.add(new Country("Saudi Arabia", "sa","data:image/png;base64,"));
    }

    public void getCountries(){
        setCountriesList();
        countriesListLiveData.setValue(countriesList);
    }

}
